import os
import streamlit as st
from PIL import Image
import matplotlib.pyplot as plt
import re

def extract_phrases_and_words(phrases, text):
    """
    Extracts phrases from the text based on the given list, ensuring each phrase is checked only once.
    Then, it extracts remaining words and punctuation separately.
    """
    found_phrases = []
    remaining_text = text

    for phrase in phrases:
        if phrase in remaining_text:
            found_phrases.append(phrase)
            remaining_text = remaining_text.replace(phrase, " ")  # Remove found phrase once

    words_and_punctuation = re.findall(r'[\u0900-\u097F]+|[.,!?;:"]', remaining_text.strip())
    found_phrases.extend(words_and_punctuation)  # Combine lists

    return found_phrases

def fetch_all_files_from_drive(drive_path, file_extensions=(".jpg", ".png")):
    """
    Fetch all image files from the specified drive path.
    """
    files_list = []
    for root, dirs, files in os.walk(drive_path):
        for file in files:
            if file.endswith(file_extensions):
                files_list.append(os.path.join(root, file))
    return files_list

symbol_mapping = {
    ".": "dot",
    ",": "comma",
    "?": "question_mark",
    "!": "exclamation",
    ";": "semicolon",
    ":": "colon"
}

def display_images(image_files, phrases, image_size=(300, 300)):
    """
    Display images in order of found phrases.
    """
    num_images = len(phrases)
    if num_images == 0:
        st.warning("कोणतेही वाक्य आढळले नाही. प्रतिमा दर्शविण्यात येणार नाहीत.")
        return

    cols = st.columns(num_images)  # Create columns dynamically

    for i, phrase in enumerate(phrases):
        filename = symbol_mapping.get(phrase, phrase)  # Map punctuation to filename if exists
        matching_files = [file for file in image_files if os.path.basename(file).split('.')[0] == filename]

        if matching_files:
            image_path = matching_files[0]
            try:
                image = Image.open(image_path).resize(image_size)
                cols[i].image(image, caption=phrase)
            except Exception as e:
                st.error(f"'{phrase}' साठी प्रतिमा लोड करताना त्रुटी: {e}")
        else:
            st.warning(f"'{phrase}' साठी प्रतिमा सापडली नाही.")

